mapper.select:
select hos_id, hos_name, hos_medicine, hos_linkphone, hos_logo, hos_grade, (select data_name from bs_basedict where dict_code = 'HOS01' and data_code = hos_grade) hos_grade_name, (select name from city where city_id = hos_city) hos_city_name, hos_city, (select name from area where area_id = hos_area) hos_area_name, hos_area, hos_desc, hos_awards, crtuser, crtdate from hospital where recsts = 1
and hos_name like concat('%', #{hosName}, '%')
and hos_city = #{hosCity}
and hos_area = #{hosArea}

mapper.update:
update hospital set hos_name = #{hosName}, hos_grade = #{hosGrade}, hos_city = #{hosCity}, hos_area = #{hosArea}, hos_linkphone = #{hosLinkphone}, hos_medicine = #{hosMedicine}, hos_awards = #{hosAwards},
hos_logo = #{hosLogo},
hos_desc = #{hosDesc}, upduser = #{upduser}, `update` = now() where hos_id = #{hosId}
